; 23456789↑↓←→@
secretSymbols: ; $00a1
	.asc "BDFGHJLM"
	.db $13 $bd $12 $11 $23
	.asc "NQRSTWY!"
	.db $10 $7e $7f $2b $2d
	.asc "bdfghjm$*/:~"
