;;
; The game's entrypoint.
; @addr{0150}
begin: ; 0150
	nop
	di
	cp $11
	ld a,$00
	jr nz,+

	inc a
	bit 0,b ; Check GBA mode
	jr z,+
	ld a,$ff
+
	ldh (<hGameboyType),a
	ld a,$37
	ldh (<hRng1),a
	ld a,$0d
	ldh (<hRng2),a
resetGame:
	ld sp,wMainStackTop
	jpfrombank0 init


