	ld b,a			; $501f
	ld a,(wActiveRoom)		; $5020
	cp b			; $5023
	jr z,@foundRoom		; $5024
@noMatch:
	ld a,c			; $5026
	add $08			; $5027
	ld e,a			; $5029
	cp <(wStaticObjects+wStaticObjects.size)	; $502a
	jr c,@next		; $502c
