	ld a,$02		; $5102
@getNextRow:
	push af			; $5104
	ld a,e			; $5105
	and $e0			; $5106
	ld c,a			; $5108
	ld b,$20		; $5109

@getNextTile:
	ld a,(de)		; $510b
	ldi (hl),a		; $510c
	ld a,e			; $510d
	inc a			; $510e
	and $1f			; $510f
	or c			; $5111
	ld e,a			; $5112
	dec b			; $5113
	jr nz,@getNextTile		; $5114

	ld a,$20		; $5116
	call addAToDe		; $5118
	pop af			; $511b
	dec a			; $511c
	jr nz,@getNextRow		; $511d

	; Change back to bank 7, 
	ld a,$07		; $511f
	ld ($ff00+R_SVBK),a	; $5121
	pop hl			; $5123
	push de			; $5124

	; Copy the 2 rows in wTmpVramBuffer to hl (parameter to function)
	ld de,wTmpVramBuffer		; $5125
	ld a,$02		; $5128
@writeNextRow:
	push af			; $512a
	ld a,l			; $512b
	and $e0			; $512c
	ld c,a			; $512e
	ld b,$20		; $512f

@writeNextTile:
	ld a,(de)		; $5131
	ld (hl),a		; $5132
	inc e			; $5133
	ld a,l			; $5134
	inc a			; $5135
	and $1f			; $5136
	or c			; $5138
	ld l,a			; $5139
	dec b			; $513a
	jr nz,@writeNextTile	; $513b

	ld a,$20		; $513d
	rst_addAToHl			; $513f
	pop af			; $5140
	dec a			; $5141
	jr nz,@writeNextRow	; $5142

	pop de			; $5144
	pop af			; $5145
	dec a			; $5146
